package GUI;import javax.swing.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class User extends JFrame{    private JTextField updateValue;    private JButton updateButton;    private JButton createButton;    private JTextField createValue;    private JButton removeButton;    private JTextField removeName;    private JTextField updateName;    private JTextField createName;    private JPanel rootPanel;    private JButton getButton;    private JTextField getName;    private JTextArea resultTextArea;    public User() {        add(rootPanel);        setSize(700,500);        raft.maple.Client client = new raft.maple.Client(resultTextArea);        updateButton.addActionListener(new ActionListener() {            /**             * Invoked when an action occurs.             *             * @param e the event to be processed             */            @Override            public void actionPerformed(ActionEvent e) {                String searchName = updateName.getText();                String newValue = updateValue.getText();                if( searchName != null && newValue != null){                    client.update(searchName,newValue);                }            }        });        createButton.addActionListener(new ActionListener() {            /**             * Invoked when an action occurs.             *             * @param e the event to be processed             */            @Override            public void actionPerformed(ActionEvent e) {                String newName = createName.getText();                String value = createValue.getText();                if (newName != null && value != null){                    client.create(newName,value);                }            }        });        removeButton.addActionListener(new ActionListener() {            /**             * Invoked when an action occurs.             *             * @param e the event to be processed             */            @Override            public void actionPerformed(ActionEvent e) {                String name = removeName.getText();                if (name != null){                    client.delete(name);                }            }        });        getButton.addActionListener(new ActionListener() {            /**             * Invoked when an action occurs.             *             * @param e the event to be processed             */            @Override            public void actionPerformed(ActionEvent e) {                String name = getName.getText();                if (name != null){                    client.get(name);                }            }        });    }}